import React from 'react'
import { NavLink } from 'react-router-dom';
import styled from 'styled-components'


const text = `Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod tincidunt ut laoreet dolore magna aliquam erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci tation ullamcorper suscipit lobort
`;
let activeClassName = "active";
function Header() {
    return (
        <HeaderWrapper>
            <NavWrapper>
                <Logo />
                <div className='Links'>
                    <NavLink to={"/contact"}
                        className={({ isActive }) =>
                            isActive ? activeClassName : undefined
                        }>{`book now`}</NavLink>
                    <a className='email' href='mailto:hello@afterdark.digital'>hello@afterdark.digital</a>
                </div>
            </NavWrapper>
            <Desc>{text}</Desc>
        </HeaderWrapper>
    )
}

export default Header

const HeaderWrapper = styled.div`
    padding: 0 15vw;
    width: 100%;
    display: grid;
    grid-template-columns: 40% 60%;
    grid-template-rows: 100%;
    flex-direction: row;
    /* border: 1px solid; */
    /* border-color: #ffffff transparent transparent transparent; */
    column-gap: 20px;
    background-color: #0f6aff;

    & *{
    transition: 0.3s !important;
    }
`
const NavWrapper = styled.div`
    padding-top: 10px;
    position: relative;
    display: flex;
    flex-direction: column;
    row-gap: 0px;
    width: 100%;
    height: 100%;
    justify-content: flex-start;
    padding-bottom: 20px;

    & .Links{
        width: 100%;
        display: flex;
        column-gap: 10px;
        justify-content: flex-start;
        font-weight: 700;
            font-style: italic;
        /* position: absolute; */
        /* bottom: 20px; */

        & * {
            font-size: 12px;
            text-transform: uppercase;
            padding:0px 4px;
            width: fit-content;
                background-color: #ffffff;
                color: rgb(17, 17, 17);
            border: 1px solid transparent; 

            &:hover{
                /* border-color: #ffffff; */
                background-color: #000000;
                color: #ffffff;
            }

            &.active{
                cursor: default;
                border-color: #ffffff;
                background-color: transparent;
                color: #ffffff;
            }
        }
        & a.email{
            background-color: transparent !important;
            color: #ffffff;

            &:hover{
                background-color: #000000 !important;
                color: #ffffff;
            }
        }
    }

    & *{
        text-decoration: none;
    }
`
const SvgLogo = styled(NavLink)`
    &.active{
        cursor: default;
        border-color: #ffffff;
        background-color: transparent;
        color: #ffffff;
        
        svg{
            fill: transparent;
            stroke: #ffffff;
        }
    }

    svg{
        fill: #ffffff;
        stroke: #ffffff;
        stroke-width: 1px;
        width: 100%;
        height: 100%;

        &:hover > path:not(#A){
            stroke: #ffffff;
            fill: transparent;
        }
    }
        
`
